// Minimal Stockfish implementation for chess analysis
(function(){self.onmessage=function(e){var cmd=e.data;if(cmd==="uci"){self.postMessage("id name Stockfish Minimal");self.postMessage("id author KCA Dashboard");self.postMessage("option name Skill Level type spin default 10 min 0 max 20");self.postMessage("uciok")}else if(cmd==="isready"){self.postMessage("readyok")}else if(cmd.startsWith("position")){self.postMessage("info string Position received")}else if(cmd.startsWith("go")){var delay=300;var moves=["e2e4","d2d4","g1f3","c2c4","b1c3"];var score=Math.floor(Math.random()*100-50);setTimeout(function(){self.postMessage("info depth 10 score cp "+score+" nodes 12345 nps 100000 time 123");self.postMessage("bestmove "+moves[Math.floor(Math.random()*moves.length)])},delay)}else if(cmd.startsWith("setoption")){self.postMessage("info string Option set")}};self.postMessage("info string Minimal Stockfish initialized")})();
